addEvent ("onSetHudComponentVisible", true )function setHudComponentVisible(component, visible)	if(string.lower(component) == 'dominating') then		Dominating.getInstance():setVisible(visible)	elseif(string.lower(component) == 'login') then		LoginHud.getInstance():setVisible(visible)	endendaddEventHandler ("onSetHudComponentVisible", root , setHudComponentVisible )setHudVisible = setHudComponentVisiblefunction isHudVisible(component)	if(string.lower(component) == 'dominating') then		return Dominating.instance and Dominating.getInstance():isVisible()	elseif(string.lower(component) == 'login') then		return LoginHud.instance and LoginHud.getInstance():isVisible()	end	return falseendfunction callHud(code)	local chunk = loadstring(code)	if(chunk) then		return chunk()	endend