local super = Class("Rect", LuaObject).getSuperclass()function Rect:init()	super.init(self)	self.x = 0	self.y = 0	self.width = 0	self.height = 0	return selfendfunction Rect:getX() return self.x endfunction Rect:getY() return self.y endfunction Rect:getWidth() return self.width endfunction Rect:getHeight() return self.height endfunction Rect:getLocation()	return self.x, self.yendfunction Rect:setLocation(x, y)	self.x = x	self.y = yendfunction Rect:contains(X,Y)	local w = self.width	local h = self.height	if (w < 0 or h < 0)  then		return false	end	local x = self.x	local y = self.y	if (X < x or Y < y) then		return false	end	w = w + x	h = h + y	return ((w < x or w > X) and (h < y or h > Y))endfunction Rect:setSize(width,height)	self:setBounds(self.x,self.y,width,height)endfunction Rect:setBounds(x, y, width, height)    local resized = (self.width ~= width) or (self.height ~= height)    local moved = (self.x ~= x) or (self.y ~= y)    if (not resized and not moved) then        return    end    self.x = x    self.y = y    self.width = width    self.height = heightend