local super = Class("RenderTarget", Container).getSuperclass()function RenderTarget:init(sourceWidth, sourceHeight)	super.init(self)	self.clear = true	self:setAutoUpdate(true)	self:setSourceSize(sourceWidth, sourceHeight)	return selfendfunction RenderTarget:setSourceSize(sourceWidth, sourceHeight)	if(sourceWidth == nil) then		sourceWidth = Graphics.getInstance():getWidth()	end	if(sourceHeight == nil) then		sourceHeight = Graphics.getInstance():getHeight()	end	self.sourceWidth, self.sourceHeight = sourceWidth, sourceHeight	if(self.RenderTarget) then		destroyElement(self.RenderTarget)	end	self.RenderTarget = dxCreateRenderTarget(self.sourceWidth, self.sourceHeight, true) endfunction RenderTarget:setAutoUpdate(value)	self.autoUpdate = valueendfunction RenderTarget:paint(g)	local x, y = self:getLocationOnScreen()	local w = self:getWidth()	local h = self:getHeight()		if(self.autoUpdate) then		dxSetRenderTarget(self.RenderTarget, self.clear)		self.paintingComponents = true		self.__parent.paint(self,g)		self.paintingComponents = false		dxSetRenderTarget()	end		if self.RenderTarget then		dxDrawImage(x, y, w, h, self.RenderTarget)	endendfunction RenderTarget:getX()	if(self.paintingComponents) then return 0 end	return self.xendfunction RenderTarget:getY()	if(self.paintingComponents) then return 0 end	return self.yend