local super = Class("ScreenSource", Component).getSuperclass()ScreenSource.nameCounter = 0function ScreenSource:init(sourceWidth, sourceHeight)	super.init(self)	self:setAutoUpdate(true)	self:setSourceSize(sourceWidth, sourceHeight)	self:setBackground(tocolor(255,255,255,255))	return selfendfunction ScreenSource:setSourceSize(sourceWidth, sourceHeight)	if(sourceWidth == nil) then		sourceWidth = Graphics.getInstance():getWidth()	end	if(sourceHeight == nil) then		sourceHeight = Graphics.getInstance():getHeight()	end	self.sourceWidth, self.sourceHeight = sourceWidth, sourceHeight	if(self.screenSource) then		destroyElement(self.screenSource)	end	self.screenSource = dxCreateScreenSource(self.sourceWidth, self.sourceHeight) endfunction ScreenSource:setAutoUpdate(value)	self.autoUpdate = valueendfunction ScreenSource:paint(g)	local x, y = self:getLocationOnScreen()	local w = self:getWidth()	local h = self:getHeight()		if(self.autoUpdate) then		dxUpdateScreenSource( self.screenSource) 	end		if self.screenSource then		dxDrawImage(x, y, w, h, self.screenSource, 0, 0, 0, self:getBackground())	endend